import React, { useState } from "react";
import Axios from "axios";
import Table from "./Table";

function Corporation() {
  const [name, setName] = useState("");
  const [corporationList, setCorporationList] = useState([
    { "No Files Selected": "" },
  ]);

  const findStocks = () => {
    console.log(name);
    Axios.post("http://localhost:3000/corporations", {
      name: name,
    }).then(() => console.log("success"));
  };

  const getStocks = () => {
    console.log(name);
    Axios.get("http://localhost:3000/corporationsShow").then((response) =>
      setCorporationList(response.data)
    );
  };

  return (
    <section>
      <div className="buttonTop">
        <button onClick={getStocks} className="buttonTopStyle">
          Show All Corporations
        </button>
        <button onClick={getStocks} className="buttonTopStyle">
          Show All Corporations
        </button>
      </div>
      <div className="information">
        <label>Corporation: </label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <button onClick={findStocks}>Find Corporations</button>
      </div>

      <Table data={corporationList} />
    </section>
  );
}

export default Corporation;
