import React, { useState } from "react";
import Axios from "axios";
import Table from "./Table";

function Corporation() {
  const [name, setName] = useState("");
  //   const [newName, setNewName] = useState("");
  const [corporationList, setCorporationList] = useState([
    { "No Files Selected": "" },
  ]);

  const addCorp = async () => {
    await Axios.post("http://localhost:3000/addCorp", {
      name: name,
    })
      .then(getAll())
      .catch((error) => {
        console.log(error);
      });
    // Axios.get("http://localhost:3000/corporationsShow")
    //   .then((response) => setCorporationList(response.data))
    //   .catch((error) => {
    //     console.log(error);
    //   });
  };

  const removeCorp = async () => {
    await Axios.post("http://localhost:3000/removeCorp", {
      name: name,
    })
      .then(getAll())
      .catch((error) => {
        console.log(error);
      });
    // Axios.get("http://localhost:3000/corporationsShow")
    //   .then((response) => setCorporationList(response.data))
    //   .catch((error) => {
    //     console.log(error);
    //   });
  };

  const getAll = () => {
    console.log("Getting all corporate Shares");
    Axios.get("http://localhost:3000/corporationsShow")
      .then((response) => setCorporationList(response.data))
      .catch((error) => {
        console.log(error);
      });
  };

  const getShares = () => {
    Axios.get("http://localhost:3000/corporationsShares")
      .then((response) => setCorporationList(response.data))
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <section>
      <div className="buttonTop">
        <button onClick={getAll} className="buttonTopStyle">
          Show All Corporations
        </button>
        <button onClick={getShares} className="buttonTopStyle">
          Show All Corporations Shares
        </button>
      </div>

      <div className="information">
        <label>Add Corporation</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
          placeholder="Name"
        />
        <button onClick={addCorp}>Add Corporations</button>
      </div>

      <div className="information">
        <label>Remove Corporation</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
          placeholder="Name"
        />
        <button onClick={removeCorp}>Remove Corporations</button>
      </div>

      <div className="tableOutput">
        <Table data={corporationList} />
      </div>
    </section>
  );
}

export default Corporation;
