import React, { useState } from "react";
import Axios from "axios";
import Table from "./Table";

function Corporation() {
  const [name, setName] = useState("");
  const [newName, setNewName] = useState("");
  const [corporationList, setCorporationList] = useState([
    { "No Files Selected": "" },
  ]);

  const addCorp = () => {
    Axios.post("http://localhost:3000/addCorp", {
      name: newName,
    }).then(() => console.log("success"));
  };

  const getAll = () => {
    console.log(name);
    Axios.get("http://localhost:3000/corporationsShow").then((response) =>
      setCorporationList(response.data)
    );
  };

  const getShares = () => {
    console.log(name);
    Axios.get("http://localhost:3000/corporationsShares").then((response) =>
      setCorporationList(response.data)
    );
  };

  return (
    <section>
      <div className="buttonTop">
        <button onClick={getAll} className="buttonTopStyle">
          Show All Corporations
        </button>
        <button onClick={getShares} className="buttonTopStyle">
          Show All Corporations Shares
        </button>
      </div>

      <div className="information">
        <label>Add Corporation</label>
        <input
          type="text"
          onChange={(event) => {
            setNewName(event.target.value);
          }}
          placeholder="Name"
        />
        <button onClick={addCorp}>Add Corporations</button>
      </div>
      <div className="tableOutput">
        <Table data={corporationList} />
      </div>
    </section>
  );
}

export default Corporation;
